@media screen and (max-width: 768px) {
  // Default CSS
  body {
    font-size: 16px;
  }
  section {
    padding: 0px 15px;
  }
  .inner {
    width: 100%;
  }
  // Common CSS
  .headline-share h1 {
    font-size: 25px;
    position: relative;
    padding-bottom: 20px;
    margin-bottom: 0;
  }
  .headline-share h1::after {
    content: '';
    position: absolute;
    height: 5px;
    width: 40px;
    background: linear-gradient(to right, #1f8de6, #5ed6fe);
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
  }
  .headline-share p {
    font-size: 16px;
    margin: 0;
  }
  .back-to-position {
    opacity: 0;
    transition: 0.5s;
  }
  .show .back-to-position {
    opacity: 1;
  }
  .back-to-position.to-right {
    transform: translateX(0px);
  }
  .show .back-to-position.to-right {
    transform: translateX(0);
  }
  .back-to-position.to-left {
    transform: translateX(0px);
  }
  .show .back-to-position.to-left {
    transform: translateX(0);
  }
  .back-to-position.to-down {
    transform: translateY(0px);
  }
  .show .back-to-position.to-down {
    transform: translateY(0);
  }
  .show .back-to-position.to-down.delay-0 {
    transition-delay: 0s;
  }
  .show .back-to-position.to-down.delay-1 {
    transition-delay: 0.3s;
  }
  .show .back-to-position.to-down.delay-2 {
    transition-delay: 0.5s;
  }
  /* Header */
  header {
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-template-rows: auto;
    align-items: center;
    .inner {
      grid-template-columns: 100% 0;
      grid-template-rows: auto;
      order: 2;
      text-align: right;
      .header-logo {
        margin-right: 20px;
        width: 100%;
        img {
          padding-right: 20px;
        }
      }
      .header-menu {
        position: fixed;
        height: 100vh;
        width: 250px;
        top: 0;
        left: -270px;
        box-shadow: 3px 0 3px rgba(0, 0, 0, 0.15);
        padding-top: 70px;
        padding-left: 16px;
        background-color: #fff;
        transition: 0.5s;
        a {
          display: block;
          text-align: left;
        }
      }
      .header-menu.active {
        left: 0;
      }
    }
    .trigger {
      order: 1;
      height: 30px;
      margin-left: 20px;
      font-size: 28px;
      z-index: 10;
    }
    .trigger::after {
      content: '\e91c';
      font-family: xeicon;
    }
    .trigger.active::after {
      content: '\e921';
      font-family: xeicon;
    }
  }

  /* WELCOME */
  .welcome {
    background-color: #f9f9f9;
    height: 100vh;
    .swiper-container {
      display: none;
      .swiper-slide {
        min-width: 100%;
      }
    }
    .welcome-heading {
      width: 100%;
      left: 50%;
      transform: translate(-50%, -50%);
      span {
        font-size: 16px;
      }
      h1 {
        font-size: 30px;
        em {
          font-size: 22px;
        }
      }
      p {
        padding: 0 30px;
      }
    }
  }

  // Section: ceo-access
  .ceo-access {
    .ceo-inner {
      .ceo-content {
        grid-template-columns: 1fr;
        grid-template-rows: auto;
        padding: 50px 0;
        .ceo-left {
          width: 100%;
          text-align: center;
          padding-right: 0;
          h3 {
            font-size: 25px;
          }
          p {
            font-size: 16px;
          }
        }
        .ceo-right {
          grid-template-columns: 1fr;
          grid-template-rows: auto;
          .ceo-msg {
            width: 100%;
            h3 {
              font-size: 25px;
            }
            p {
              font-size: 16px;
            }
          }
          .ceo-photo {
            width: 100%;
            text-align: center;
            img {
              width: inherit;
            }
          }
        }
      }
    }
    .access-inner {
      .access-content {
        grid-template-columns: 1fr;
        grid-template-rows: auto;
        .access-left {
          width: 100%;
          padding-right: 0;
          text-align: center;
          h2 {
            font-size: 25px;
          }
          p {
            font-size: 16px;
          }
          .btn-download {
            a {
              width: 150px;
              img {
                width: inherit;
              }
            }
          }
        }
        .access-right {
          width: 100%;
          padding-top: 100px;
        }
      }
    }
  }

  // Section: banner
  .banner {
    min-width: 100%;
    .inner {
      .banner-content {
        grid-template-columns: 1fr;
        grid-template-rows: auto;
        padding: 50px 0;
        text-align: center;
        .tab-btn {
          width: 100%;
          overflow: hidden;
          .tabBtn {
            em {
              width: 80px;
              height: 80px;
              img {
                width: 50px;
                height: 50px;
              }
            }
            span {
              font-size: 16px;
            }
          }
        }
        .tabs {
          width: 100%;
          .tab {
            padding-left: 0;
            text-align: left;
            h2 {
              font-size: 25px;
            }
            p {
              font-size: 16px;
            }
          }
        }
      }
    }
  }

  // Section: feature
  .feature {
    .inner {
      .feature-content {
        grid-template-columns: 1fr;
        grid-template-rows: auto;
        .feature-mockup {
          img {
            width: 100%;
          }
        }
        .feature-about {
          padding: 0 20px;
          padding-top: 30px;
          h2 {
            font-size: 25px;
          }
          p {
            font-size: 16px;
          }
        }
      }
    }
  }

  // Section: vision
  .vision {
    min-width: 100%;
    .vision-content > div {
      width: 100%;
      height: auto;
    }
    .vision-content {
      grid-template-columns: 1fr;
      grid-template-rows: auto;
      .vision-item-desc {
        .vision-item-headline {
          width: 100%;
          padding: 20px;
          h3 {
            font-size: 25px;
          }
          p {
            font-size: 16px;
          }
        }
      }
    }
  }

  // Section: faq
  .faq {
    .inner {
      .faq-content {
        grid-template-columns: 1fr;
        grid-template-rows: auto;
        .faq-accordion {
          width: 100%;
          height: 100%;
          padding-right: 0;
          .faq-item {
            .faq-title {
              padding-right: 20px;
            }
            .faq-title::after {
              font-size: 16px;
            }
            .faq-desc {
              width: 100%;
              font-size: 15px;
            }
          }
          .faq-item.active {
            .faq-desc {
              height: 350px;
            }
          }
        }
      }
      .award-content {
        width: 100%;
        margin: 50px 0;
        h2 {
          font-size: 25px;
        }
        p {
          font-size: 16px;
        }
        .award-image::after {
          width: 150px;
          font-size: 14px;
        }
      }
    }
  }

  // Section : review
  .review {
    min-width: 100%;
    .inner {
      .review-content {
        width: 100%;
        .slogan {
          margin-top: 20px;
          font-size: 22px;
        }
        .swiper-container {
          width: 100%;
          .swiper-slide {
            padding: 0;
            p {
              font-size: 16px;
            }
          }
        }
        .swiper-button-prev {
          display: none;
        }
        .swiper-button-next {
          display: none;
        }
      }
    }
  }

  // Section: focus
  .focus {
    .inner {
      .focus-content {
        .focus-item {
          display: block;
          width: 100%;
          margin-top: 50px;
          h3 {
            font-size: 20px;
          }
          p {
            padding: 0 10px;
          }
        }
      }
    }
  }

  // Section: guide
  .guide {
    padding-right: 0;
    .inner {
      .guide-content {
        grid-template-columns: 1fr;
        grid-template-rows: auto;
        text-align: center;
        .guide-slider-mockup {
          padding-left: 0;
          top: 0%;
          left: 50%;
          transform: translateX(-50%);
          .swiper-container {
            top: 105px;
            left: 0;
            right: 0;
            bottom: 0;
            margin: auto;
            .swiper-wrapper {
              .swiper-slide {
                img {
                  width: 100%;
                }
              }
            }
          }
        }
        .guide-video {
