        grid-template-columns: 1fr;
        grid-template-rows: auto;
        .news-left {
          margin-bottom: 50px;
          .news-big {
            width: 100%;
            img {
              width: inherit;
            }
            .news-headline {
              h2 {
                font-size: 30px;
              }
              p {
                font-size: 16px;
                .date {
                  font-size: 14px;
                }
              }
            }
          }
          .news-big::after {
            width: 100%;
            position: absolute;
            top: 0;
            left: 0;
            background-color: rgba(0, 0, 0, 0.5);
            transition: 0.3s;
          }
          .news-big:hover::after {
            background-color: rgba(0, 0, 0, 0.3);
          }
        }
        .news-right {
          .news-items > div {
            width: 100%;
          }
          .news-items {
            flex-direction: column;
            .news-thum {
              width: 100%;
              .news-photo {
                width: 100%;
                img {
                  width: 100%;
                }
              }
            }
          }
        }
      }
    }
  }

  // Footer
  footer {
    min-width: 100%;
    padding: 100px 0 50px 0;
    .inner {
      .footer-content > div {
        padding: 0 10px;
      }
      .footer-content {
        grid-template-columns: 1fr;
        grid-template-rows: auto;
        .footer-logo {
          padding-bottom: 10px;
          text-align: left;
        }
        .copyright {
          width: 100%;
          .policy-sns {
            grid-template-columns: 1fr;
            grid-template-rows: auto;
            .sns {
              text-align: center;
              padding-top: 20px;
            }
          }
          .address {
            span {
              font-size: 13px;
            }
          }
        }
      }
    }
  }

  // Video Modal
  .video-modal.active {
    i {
      right: 20px;
    }
    .video-content {
      iframe {
        width: 350px;
        height: 200px;
      }
    }
  }

  // Go to Top
  .btn-top {
    display: none;
  }
}
