/* Google Web font */
@import url("https://fonts.googleapis.com/css2?family=Open+Sans:wght@300;400;600&display=swap");
/* Web font Noonnu */
@font-face {
  font-family: "NEXON Lv1 Gothic OTF";
  src: url("https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_20-04@2.1/NEXON Lv1 Gothic OTF.woff") format("woff");
  font-weight: normal;
  font-style: normal;
}
/* Reset CSS */
* {
  box-sizing: border-box;
}

a {
  text-decoration: none;
  color: #222;
}

button,
input {
  outline: none;
}

h1,
h2,
h3,
h4,
h5,
h6 {
  margin-top: 0;
  font-weight: normal;
  line-height: 1.5em;
}

/* Default CSS */
html {
  scroll-behavior: smooth;
}

body {
  font-family: "NEXON Lv1 Gothic OTF", "Open Sans", sans-serif;
  font-size: 18px;
  line-height: 1.7em;
  margin: 0;
  background-color: #fff;
  color: #222;
  position: relative;
}

body.active {
  overflow: hidden;
}

/* Common CSS */
.inner {
  width: 1300px;
  margin: auto;
}

.btn {
  background-color: #fff;
  padding: 10px;
  border-radius: 5px;
  transition: 0.3s;
  display: inline-block;
}

.headline-share {
  text-align: center;
  margin-top: 100px;
}
.headline-share h1 {
  font-size: 38px;
  position: relative;
}
.headline-share h1::after {
  content: "";
  width: 100px;
  height: 5px;
  border-radius: 3px;
  background: linear-gradient(to right, #54a0ff, #487eb0);
  position: absolute;
  bottom: -25px;
  left: 50%;
  transform: translateX(-50%);
}
.headline-share p {
  padding-top: 30px;
  font-size: 20px;
}

.back-to-position {
  opacity: 0;
  transition: 0.5s;
}

.show .back-to-position {
  opacity: 1;
}

.back-to-position.to-right {
  transform: translateX(-100px);
}

.show .back-to-position.to-right {
  transform: translateX(0);
}

.back-to-position.to-left {
  transform: translateX(100px);
}

.show .back-to-position.to-left {
  transform: translateX(0);
}

.back-to-position.to-down {
  transform: translateY(-100px);
}

.show .back-to-position.to-down {
  transform: translateY(0);
}

.show .back-to-position.to-down.delay-0 {
  transition-delay: 0s;
}

.show .back-to-position.to-down.delay-1 {
  transition-delay: 0.3s;
}

.show .back-to-position.to-down.delay-2 {
  transition-delay: 0.5s;
}

/* #################### Header #################### */
header {
  position: fixed;
  width: 100%;
  z-index: 15;
  transition: 0.3s;
}
header .inner {
  display: grid;
  grid-template-columns: 40% 60%;
  grid-template-rows: auto;
  padding-top: 30px;
  padding-bottom: 15px;
}
header .header-logo img {
  margin-top: -7px;
}
header .header-menu a {
  margin: 10px;
  font-size: 15px;
}

header.active {
  background-color: #fff;
  box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.15);
}

/* #################### WELCOME #################### */
.welcome {
  position: relative;
  height: 90vh;
}
.welcome .swiper-slide {
  min-width: 1300px;
}
.welcome .swiper-slide img {
  width: 100%;
}
.welcome .welcome-heading {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  left: 200px;
  text-align: center;
  width: 750px;
  z-index: 5;
}
.welcome .welcome-heading span {
  font-size: 24px;
}
.welcome .welcome-heading h1 {
  font-family: "Open Sans", sans-serif;
  font-size: 55px;
  font-weight: 600;
  line-height: 1.2em;
  margin: 15px 0;
}
.welcome .welcome-heading h1 em {
  display: block;
  color: #22b8cf;
  font-size: 55px;
  margin-top: 10px;
}
.welcome .welcome-heading p {
  padding: 0 100px;
  margin-bottom: 50px;
}
.welcome .welcome-heading .welcome-btn .btn:hover {
  transform: translateY(-10px);
  box-shadow: 0 3px 0 rgba(0, 0, 0, 0.015);
}
.welcome .welcome-heading .welcome-btn .btn--start {
  background-color: #22b8cf;
  color: #fff;
  margin-right: 10px;
}
.welcome .mouse {
  position: absolute;
  bottom: 100px;
  left: 250px;
  width: 35px;
  height: 45px;
  border: 2px solid #fff;
  border-radius: 15px;
  z-index: 5;
}
.welcome .mouse .wheele {
  width: 3px;
  height: 12px;
  top: 15px;
  left: 14px;
  border-radius: 3px;
  position: absolute;
  background-color: #fff;
  animation: wheele 1.5s linear infinite;
}

@keyframes wheele {
  0% {
    top: 15px;
  }
  50% {
    top: 22px;
  }
  100% {
    top: 15px;
  }
}
/* #################### section : ceo-access #################### */
.ceo-access .ceo-inner {
  padding: 50px 0;
  border-bottom: 2px solid #f2f2f2;
}
.ceo-access .ceo-inner .ceo-content > div {
  width: 50%;
}
.ceo-access .ceo-inner .ceo-content {
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-template-rows: auto;
}
.ceo-access .ceo-inner .ceo-content .ceo-left {
  width: 100%;
  margin-right: 0;
  padding-right: 80px;
}
.ceo-access .ceo-inner .ceo-content .ceo-left h3 {
  font-size: 20px;
  font-weight: 600;
}
.ceo-access .ceo-inner .ceo-content .ceo-left p {
  font-size: 16px;
}
.ceo-access .ceo-inner .ceo-content .ceo-right {
  width: 100%;
  display: grid;
  grid-template-columns: 60% 40%;
  grid-template-rows: auto;
  text-align: center;
}
.ceo-access .ceo-inner .ceo-content .ceo-right .ceo-msg {
  padding: 20px;
}
.ceo-access .ceo-inner .ceo-content .ceo-right .ceo-msg h3 {
  font-size: 28px;
  font-weight: 600;
  color: #12b886;
}
.ceo-access .ceo-inner .ceo-content .ceo-right .ceo-msg h3 big {
  display: block;
}
.ceo-access .ceo-inner .ceo-content .ceo-right .ceo-msg p {
  font-size: 16px;
}
.ceo-access .ceo-inner .ceo-content .ceo-right .ceo-photo {
  text-align: right;
  padding: 20px;
}
.ceo-access .ceo-inner .ceo-content .ceo-right .ceo-photo img {
  width: 100%;
  border-radius: 15px;
}
.ceo-access .access-inner {
  padding-top: 100px;
}
.ceo-access .access-inner .access-content {
  display: grid;
  grid-template-columns: 50% 50%;
  grid-template-rows: auto;
}
.ceo-access .access-inner .access-content .access-left {
  padding-right: 70px;
}
.ceo-access .access-inner .access-content .access-left h2 {
  font-size: 28px;
  font-weight: 400;
}
.ceo-access .access-inner .access-content .access-left p {
  font-size: 16px;
  padding: 10px 0;
}
.ceo-access .access-inner .access-content .access-left .btn-download a {
  width: 200px;
  margin-right: 10px;
}
.ceo-access .access-inner .access-content .access-left .btn-download a::last-child {
  margin-right: 0;
}
.ceo-access .access-inner .access-content .access-right {
  text-align: right;
}
.ceo-access .access-inner .access-content .access-right img {
  width: 100%;
  display: block;
}

/* #################### section : banner #################### */
.banner {
  background: linear-gradient(to right, #54a0ff, #487eb0);
  min-width: 1300px;
}
.banner .inner .banner-content > div {
  margin: 30px 0;
}
.banner .inner .banner-content {
  display: grid;
  grid-template-columns: 50% 50%;
  grid-template-rows: auto;
  padding: 20px;
  color: #fff;
}
.banner .inner .banner-content .tab-btn > div {
  text-align: center;
}
.banner .inner .banner-content .tab-btn {
  display: grid;
  grid-template-columns: 33% 33% 33%;
  grid-template-rows: auto;
}
.banner .inner .banner-content .tab-btn em {
  display: inline-block;
  width: 120px;
  height: 120px;
  background: #fff;
  border-radius: 50%;
  text-align: center;
  padding-top: 15px;
  margin-bottom: 20px;
  cursor: pointer;
}
.banner .inner .banner-content .tab-btn em img {
  width: 80px;
  height: 80px;
  filter: invert(53%) sepia(61%) saturate(1740%) hue-rotate(193deg) brightness(103%) contrast(103%);
}
.banner .inner .banner-content .tab-btn span {
  display: block;
  font-size: 16px;
  padding: 0 5px;
}
.banner .inner .banner-content .tab-btn .tab1.active em,
.banner .inner .banner-content .tab-btn .tab2.active em,
.banner .inner .banner-content .tab-btn .tab3.active em {
  background: #192a56;
}
.banner .inner .banner-content .tab-btn .tab1.active img,
.banner .inner .banner-content .tab-btn .tab2.active img,
.banner .inner .banner-content .tab-btn .tab3.active img {
  filter: invert(1);
}
.banner .inner .banner-content .tab-btn .tab1.active span,
.banner .inner .banner-content .tab-btn .tab2.active span,
.banner .inner .banner-content .tab-btn .tab3.active span {
  color: #192a56;
}
.banner .inner .banner-content .tabs .tab {
  display: none;
  padding-left: 50px;
  margin-top: 25px;
}
.banner .inner .banner-content .tabs .tab1 h2,
.banner .inner .banner-content .tabs .tab2 h2,
.banner .inner .banner-content .tabs .tab3 h2 {
  font-weight: bold;
  font-size: 32px;
  padding-bottom: 10px;
}
.banner .inner .banner-content .tabs .tab1 p,
.banner .inner .banner-content .tabs .tab2 p,
.banner .inner .banner-content .tabs .tab3 p {
  font-size: 17px;
}
.banner .inner .banner-content .tabs .tab1.active,
.banner .inner .banner-content .tabs .tab2.active,
.banner .inner .banner-content .tabs .tab3.active {
  display: block;
}

/* #################### section : feature #################### */
.feature .inner .feature-content > div {
  text-align: center;
}
.feature .inner .feature-content {
  display: grid;
  grid-template-columns: 50% 50%;
  margin: 150px 0;
}
.feature .inner .feature-content .feature-about {
  padding: 0 50px;
  padding-top: 80px;
}

/* #################### section :  vision #################### */
.vision {
  min-width: 1300px;
}
.vision .vision-content > div {
  height: 400px;
}
.vision .vision-content {
  display: grid;
  grid-template-columns: 33.333333% 33.333333% 33.333333%;
  grid-template-rows: auto;
}
.vision .vision-content .vision-item-desc {
  background-color: #f9f9f9;
}
.vision .vision-content .vision-item-desc .vision-item-headline {
  width: 60%;
  margin: auto;
  margin-top: 100px;
}
.vision .vision-content .vision-item-desc .vision-item-headline h3 {
  font-size: 28px;
  margin-bottom: 0;
}
.vision .vision-content .vision-item-desc .vision-item-headline hr {
  width: 50px;
  height: 5px;
  background-color: #5f27cd;
  display: inline-block;
  margin: 15px 0;
}
.vision .vision-content .vision-item-desc .vision-item-headline p {
  margin-top: 0;
}
.vision .vision-content .vision-item-photo img {
  width: 100%;
  object-fit: cover;
  height: inherit;
}

/* #################### section : faq #################### */
.faq .inner {
  overflow: hidden;
}
.faq .inner .faq-content {
  display: grid;
  grid-template-columns: 50% 50%;
  grid-template-rows: auto;
  padding: 20px;
}
.faq .inner .faq-content .faq-accordion {
  padding-right: 50px;
  height: 680px;
}
.faq .inner .faq-content .faq-accordion .faq-item {
  border: 1px solid #c8d6e5;
  border-radius: 10px;
  padding: 5px 10px;
  margin-bottom: 15px;
  position: relative;
  cursor: pointer;
}
.faq .inner .faq-content .faq-accordion .faq-item .faq-title {
  font-size: 18px;
  padding: 12px 10px;
  transition: 0.3s;
}
.faq .inner .faq-content .faq-accordion .faq-item .faq-desc {
  font-size: 16px;
  overflow: hidden;
  height: 0;
  transition: 0.5s;
}
.faq .inner .faq-content .faq-accordion .faq-title::after {
  content: "\e93e";
  font-family: xeicon;
  position: absolute;
  top: 15px;
  right: 20px;
  font-size: 18px;
  transition: 0.3s;
}
.faq .inner .faq-content .faq-accordion .faq-item.active {
  border: 1px solid #54a0ff;
}
.faq .inner .faq-content .faq-accordion .faq-item.active .faq-title {
  color: #54a0ff;
}
.faq .inner .faq-content .faq-accordion .faq-item.active .faq-title::after {
  transform: rotate(90deg);
}
.faq .inner .faq-content .faq-accordion .faq-item.active .faq-desc {
  height: 240px;
  transition: 0.5s;
}
.faq .inner .award-content {
  text-align: center;
  padding: 20px;
}
.faq .inner .award-content .award-image {
  position: relative;
}
.faq .inner .award-content .award-image img {
  width: 100%;
  object-fit: cover;
}
.faq .inner .award-content .award-image::after {
  content: "december 9, 2020";
  padding: 15px;
  background: #222;
  text-transform: uppercase;
  color: #fff;
  position: absolute;
  bottom: -20px;
  left: 50%;
  transform: translateX(-50%);
}
.faq .inner .award-content h2 {
  margin-top: 40px;
  font-size: 38px;
  color: #feca57;
  margin-bottom: 0;
}
.faq .inner .award-content p {
  margin: 0;
  margin-top: 20px;
}
.faq .inner .award-content .btn-awards {
  border: 1px solid #000;
  border-radius: 5px;
  padding: 10px 20px;
  display: inline-block;
  margin-top: 20px;
  font-size: 16px;
}

/* #################### section : review #################### */
.review {
  min-width: 1300px;
  background: #f2f2f2;
  padding: 50px 20px;
}
.review .inner .review-content {
  text-align: center;
}
.review .inner .review-content .slogan {
  font-size: 30px;
}
.review .inner .review-content .swiper-container {
  overflow: hidden;
  width: 70%;
  margin: auto;
  position: relative;
  padding-bottom: 50px;
}
.review .inner .review-content .swiper-container .swiper-slide {
  padding: 0 100px;
}
.review .inner .review-content .swiper-container .swiper-slide p {
  font-size: 18px;
}
.review .inner .review-content .swiper-container .swiper-slide p span {
  font-weight: 600;
  display: block;
}
.review .inner .review-content .swiper-container .swiper-button-prev {
  position: absolute;
}
.review .inner .review-content .swiper-container .swiper-button-prev::after {
  content: "\e93c";
  font-family: xeicon;
  width: 20px;
  height: 20px;
  position: absolute;
  color: #222;
  top: -60px;
}
.review .inner .review-content .swiper-container .swiper-button-next {
  position: absolute;
}
.review .inner .review-content .swiper-container .swiper-button-next::after {
  content: "\e93f";
  font-family: xeicon;
  width: 20px;
  height: 20px;
  position: absolute;
  color: #222;
  top: -60px;
  right: 20px;
}
.review .inner .review-content .swiper-container .swiper-pagination {
  position: absolute;
  bottom: 15px;
}
.review .inner .review-content .swiper-container .swiper-pagination-bullet {
  width: 12px;
  height: 12px;
  border: 1px solid #000;
  background: #222;
}

/* #################### section : focus #################### */
.focus .inner .focus-content {
  text-align: center;
  margin-top: 50px;
  padding-bottom: 80px;
  border-bottom: 1px solid #f2f2f2;
}
.focus .inner .focus-content .focus-item {
  display: inline-block;
  margin-top: 20px;
  width: 30%;
}
.focus .inner .focus-content .focus-item img {
  width: 80px;
  height: 80px;
  filter: invert(76%) sepia(79%) saturate(908%) hue-rotate(334deg) brightness(103%) contrast(94%);
}
.focus .inner .focus-content .focus-item h3 {
  margin: 10px 0;
}
.focus .inner .focus-content .focus-item i {
  transform: rotate(45deg);
  font-size: 12px;
}
.focus .inner .focus-content .focus-item p {
  margin-top: 10px;
  padding: 0 30px;
  font-size: 16px;
}

/* #################### section : guide #################### */
.guide .inner .guide-content {
  display: grid;
  grid-template-columns: 50% 50%;
  grid-template-rows: auto;
}
.guide .inner .guide-content .guide-slider-mockup {
  position: relative;
  display: inline-block;
  padding-top: 100px;
  padding-left: 200px;
}
.guide .inner .guide-content .guide-slider-mockup .swiper-container {
  position: absolute;
  overflow: hidden;
  border-radius: 15px;
  top: 150px;
  left: 220px;
  width: 280px;
  height: 580px;
}
.guide .inner .guide-content .guide-slider-mockup .swiper-container .swiper-wrapper .swiper-slide img {
  width: 100%;
  border-radius: 15px;
}
.guide .inner .guide-content .guide-video {
  padding-top: 100px;
}
.guide .inner .guide-content .guide-video h1 {
  font-size: 38px;
}
.guide .inner .guide-content .guide-video h1 span {
  color: crimson;
}
.guide .inner .guide-content .guide-video h2 {
  font-size: 28px;
}
.guide .inner .guide-content .guide-video h2 img {
  margin-right: 10px;
  transform: translateY(5px);
}
.guide .inner .guide-content .guide-video .video-frame {
  width: 530px;
  height: 380px;
  position: relative;
}
.guide .inner .guide-content .guide-video .video-frame img {
  width: inherit;
  height: inherit;
  object-fit: cover;
  border-radius: 10px;
}
.guide .inner .guide-content .guide-video .video-frame i {
  position: absolute;
  top: 50%;
  left: 48%;
  color: #fff;
  opacity: 0.5;
  transform: translate(-50%, -45%);
  font-size: 85px;
  cursor: pointer;
}

.video-modal {
  position: fixed;
  transition: 0.5s;
  height: 0;
  opacity: 0;
}

.video-modal.active {
  position: fixed;
  display: block;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: #000;
  z-index: 15;
  transition: 0.5s;
  opacity: 1;
}
.video-modal.active i {
  position: absolute;
  color: #fff;
  font-size: 30px;
  top: 50px;
  right: 50px;
  cursor: pointer;
}
.video-modal.active .video-content {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}
.video-modal.active .video-content iframe {
  width: 900px;
  height: 506px;
}

/* #################### section : news #################### */
.news .inner .news-content {
  display: grid;
  grid-template-columns: 50% 50%;
  grid-template-rows: auto;
  margin-top: 100px;
  margin-bottom: 50px;
}
.news .inner .news-content .news-left .news-big {
  width: 550px;
  height: 550px;
  position: relative;
  margin: auto;
}
.news .inner .news-content .news-left .news-big img {
  width: inherit;
  height: inherit;
  object-fit: cover;
}
.news .inner .news-content .news-left .news-big .news-headline {
  position: absolute;
  top: 50%;
  left: 50%;
  width: 80%;
  text-align: center;
  color: #fff;
  transform: translate(-50%, -50%);
  z-index: 5;
}
.news .inner .news-content .news-left .news-big .news-headline h2 {
  font-size: 28px;
}
.news .inner .news-content .news-left .news-big .news-headline p {
  font-size: 18px;
}
.news .inner .news-content .news-left .news-big .news-headline p .date {
  display: block;
  font-size: 16px;
  padding-top: 25px;
  padding-bottom: 10px;
  text-transform: uppercase;
}
.news .inner .news-content .news-left .news-big .news-headline .btn-view {
  background-color: #000;
  color: #fff;
  font-size: 16px;
  padding: 12px 20px;
  border-radius: 5px;
}
.news .inner .news-content .news-left .news-big::after {
  content: "";
  width: 550px;
  height: 550px;
  position: absolute;
  top: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.5);
  transition: 0.3s;
}
.news .inner .news-content .news-left .news-big:hover::after {
  background-color: rgba(0, 0, 0, 0.3);
}
.news .inner .news-content .news-right .news-items > div {
  width: 50%;
}
.news .inner .news-content .news-right .news-items {
  display: flex;
}
.news .inner .news-content .news-right .news-items .news-thum {
  width: 280px;
  cursor: pointer;
}
.news .inner .news-content .news-right .news-items .news-thum .news-photo {
  position: relative;
  overflow: hidden;
}
.news .inner .news-content .news-right .news-items .news-thum .news-photo img {
  width: inherit;
  object-fit: cover;
  transition: 0.3s;
  display: block;
}
.news .inner .news-content .news-right .news-items .news-thum .news-photo span {
  position: absolute;
  color: #fff;
  background-color: #000;
  font-size: 12px;
  left: 0;
  bottom: 0px;
  padding: 2px 10px;
}
.news .inner .news-content .news-right .news-items .news-thum .news-photo span.hot {
  background-color: crimson;
}
.news .inner .news-content .news-right .news-items .news-thum p {
  font-size: 17px;
}
.news .inner .news-content .news-right .news-items .news-thum p .date {
  display: block;
  text-align: right;
  font-size: 15px;
}
.news .inner .news-content .news-right .news-items .news-thum:first-child {
  margin-right: 40px;
}
.news .inner .news-content .news-right .news-items .news-thum:hover img {
  transform: scale(1.1);
}
.news .inner .btn-readmore {
  display: block;
  text-align: center;
  margin: auto;
  width: 180px;
  background-color: #487eb0;
  text-transform: uppercase;
  font-size: 14px;
  padding: 10px 20px;
  color: #fff;
  border-radius: 25px;
}

/* #################### footer #################### */
footer {
  min-width: 1300px;
  margin-top: 100px;
  background-color: #21325b;
  color: #fff;
  padding: 100px 0;
}
footer .inner .footer-content {
  display: grid;
  grid-template-columns: 20% 80%;
}
footer .inner .footer-content .footer-logo {
  text-align: center;
}
footer .inner .footer-content .footer-logo img {
  filter: grayscale(1);
}
footer .inner .footer-content .copyright .policy-sns {
  display: grid;
  grid-template-columns: 1fr 1fr;
  padding-bottom: 20px;
  border-bottom: 1px solid #f8f9fa2a;
}
footer .inner .footer-content .copyright .policy-sns .policy span {
  font-size: 15px;
  color: #868e96;
}
footer .inner .footer-content .copyright .policy-sns .policy span a {
  color: #868e96;
}
footer .inner .footer-content .copyright .policy-sns .policy span a:first-child {
  padding-right: 10px;
}
footer .inner .footer-content .copyright .policy-sns .policy span a:last-child {
  padding-left: 10px;
}
footer .inner .footer-content .copyright .policy-sns .policy span a:hover {
  text-decoration: underline;
}
footer .inner .footer-content .copyright .policy-sns .policy span:last-child {
  display: block;
}
footer .inner .footer-content .copyright .policy-sns .sns {
  text-align: right;
}
footer .inner .footer-content .copyright .policy-sns .sns i {
  color: #fff;
  width: 30px;
  height: 30px;
  background-color: rgba(255, 255, 255, 0.2);
  border-radius: 5px;
  text-align: center;
  line-height: 30px;
  transition: 0.3s;
}
footer .inner .footer-content .copyright .policy-sns .sns i:hover {
  background-color: rgba(0, 0, 0, 0.8);
}
footer .inner .footer-content .copyright .address {
  padding-top: 20px;
}
footer .inner .footer-content .copyright .address span {
  color: #868e96;
  display: block;
  font-size: 16px;
}

/* #################### Go to Top #################### */
.btn-top {
  position: fixed;
  bottom: 50px;
  right: -50px;
  width: 50px;
  height: 50px;
  border-radius: 50%;
  text-align: center;
  line-height: 50px;
  font-size: 25px;
  background-color: #21325b;
  color: #fff;
  cursor: pointer;
  transition: 0.4s;
}

.btn-top.active {
  right: 50px;
}

